import "./registerUser.css";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { set } from "mongoose";
export default function RegisterForm() {

    const [nombre, setNombre] = useState("");
    const [apellido, setApellido] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const navigate = useNavigate();

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setError("");
        try {
            const response = await axios.post("http://localhost:4006/api/usuarios/", {
                nombre,
                apellido,
                email,
                password
            });
            console.log(response.data);
            if (response.data.error) {
                setError(response.data.error);
            } else {
                navigate("/login");
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="container">
            <h1 className="welcome-title">Registro de Usuario</h1>
            <div className="register-card">
                <div className="card-body">
                    <h3 className="register-title">Crear una cuenta</h3>
                    <form>
                        <div className="form-group">
                            <input
                                type="text"
                                id="nombre"
                                className="form-control"
                                placeholder="Nombre"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                id="apellido"
                                className="form-control"
                                placeholder="Apellido"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type="email"
                                id="email"
                                className="form-control"
                                placeholder="Correo Electrónico"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <input
                                type="password"
                                id="password"
                                className="form-control"
                                placeholder="Contraseña"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <input
                                type="password"
                                id="confirm-password"
                                className="form-control"
                                placeholder="Confirmar Contraseña"
                                required
                            />
                        </div>
                        

                        <button className="btn btn-primary" onClick={handleSubmit} type="submit">
                            Registrarse
                        </button>
                        <p className="login-link">
                            ¿Ya tienes una cuenta?{" "}
                            <a href="/login" className="login-here">
                                Inicia sesión aquí
                            </a>
                        </p>
                    </form>
                </div>
            </div>
        </div>
    );
}
